import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import CurrencySelector from "./CurrencySelector";
import { ImageBackground, StyleSheet, View } from 'react-native';

import styles from "./Login.module.css"; // Import styles from the CSS module

function Login({ setIsloggedin, updateUsername, currency, updateCurrency }) {
  const [input, setInput] = useState("");
  const navigate = useNavigate();
  const [backgroundImage, setBackgroundImage] = useState("https://pixabay.com/photos/nature-outdoors-travel-exploration-2990060/");
  const [allImages, setAllImages] = useState([]);

  const pixabayAPIkey='38306894-c775edab4e3cdc7f3f7934971'
  let newBackground = () => {
    // sending request to the API
    fetch(
      `https://pixabay.com/api/?key=${pixabayAPIkey}&q=cat&orientation=vertical&safesearch=true&per_page=100`
      )
    .then((data) => {
      return data.json();
    })
    .then((pics) => {
        // creating a random number for index to pick one of the images from 100 we will get
        let randomImage = parseInt(Math.random() * pics.totalHits);
        // set this random image to tbe the current background
        setBackgroundImage(pics.hits[randomImage].largeImageURL);
        // set all images in state to use later for changing the background
        setAllImages(pics.hits);
      })
    .catch((err) => {
      console.log(err);
    })
    .finally(() => console.log("Finally: ", backgroundImage, allImages.length));
  };
  // executing the function only on first load once, no need for useEffect
  newBackground();

  const handleClick = () => {
    updateUsername(input);
    navigate("/search");
    setIsloggedin(true);
  };

  const styles = StyleSheet.create({
    background: {
      flex: 1,
      resizeMode: "cover",
      justifyContent: "flex-end"
    },
    app: {
      marginHorizontal: "auto",
      maxWidth: 500,
      backGroundColor: "transparent"
    }
  });

  return (
    <div className={styles.login}>
    <ImageBackground
      source={{ uri: backgroundImage }}
      style={styles.background}>
      <View style={styles.app}>
      <h1>Welcome to TravelBud!</h1>
      <p>Please enter your name to start using app</p>
      <br />
      <label>Name: </label>
      <input
        type="text"
        placeholder="e.g. John"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <br />
      <br />
      <label>Select your currency: </label>
      <CurrencySelector currency={currency} updateCurrency={updateCurrency} />
      <br />
      <br />
      <br />
      <button
        className={styles.button}
        disabled={input ? false : true}
        onClick={handleClick}
      >
        Login
      </button>
      </View>
     </ImageBackground>
    </div>
  );
}

export default Login;
