import styles from "./Map.module.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";
import "leaflet/dist/leaflet.css";

function Map({ searchedCountry, lat, lon }) {
    useEffect(() => {
      const apiGet = async () => {
        setIsLoading(true);
        try {
          const response = await mockAPI.get(
            `/holidays?&api_key=${COUNTRY_API_KEY}&country=${searchedCountry}&year=${searchedYear}`
          );
          setHolidayData(response.data.response.holidays);
          setIsLoading(false);
        } catch (error) {
          console.log(error.message);
        }
      };
      const apiGetCoords = async () => {
        setIsLoading(true);
        try {
          const response = await coords.get(
            // `/forward?access_key=${COORDS_API_KEY}&query=Taiwan`
            `https://geocode.maps.co/search?q=london`
          );
          setLat(response.data[0].lat);
          setLon(response.data[0].lon);
          setIsLoading(false);
        } catch (error) {
          console.log(error.message);
        }
      };
      apiGet();
      apiGetCoords();
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [searchedCountry, searchedYear]);
  const customIcon = new Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/447/447031.png",
    iconSize: [40, 40],
  });

  return (
    <div className={styles.container}>
      <h1>Location Map</h1>
      <MapContainer
        center={[51.5073219, -0.1276474]}
        zoom={11}
        scrollWheelZoom={false}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[51.5073219, -0.1276474]} icon={customIcon}>
          <Popup>{searchedCountry}</Popup>
        </Marker>
      </MapContainer>
    </div>
  );
}

export default Map;
