//components/AddForm.js
import { useState } from 'react';
import Joi from 'joi-browser';

function Registration({ handlerAddUser , list}) {
  const [user, setUser] = useState();
  const [error, setError] = useState("");

  const schema = {
    name: Joi.string().min(1).max(20).required(),
    email: Joi.string().email().required(),
    password:Joi.string().min(6).required(),
  }

  const validate = (e) => {
    const {name, value} = e.target;
    const objToCompare = {[name]: value};
    console.log('objToCompare', objToCompare);
    const subSchema ={[name]: schema[name]};
    console.log('subSchema', subSchema);

    const result = Joi.validate(objToCompare, subSchema);
    const {error} = result;
    return error ? error.details[0].message : null;
  }

  const handlerName = (e) => {
    const form = {...user, name: e.target.value};
    const errorMessage = validate(e);
    setError(errorMessage);
    setUser(form);
  }

  const handlerEmail = (e) => {
    const form = {...user, email: e.target.value};
    setUser(form);
  }

  const handlerPassword = (e) => {
    const form = {...user, password: e.target.value};

    setUser(form);
  }
  const handlerSubmit = (e) => {
    e.preventDefault();
    // handlerAddUser(user);

    const newlist = list.filter((item) => item.email === user.email)
    console.log(newlist);

    if (Object.keys(newlist).length === 1) {

      setError("Existing user");
    }

    else{

     handlerAddUser(user);
     setError("Succesful registered");
    }

  }
  return (
    <div>
      <form onSubmit={handlerSubmit}>
        <label>Name: </label>
        <input type="text" placeholder="e.g John" onChange={handlerName} required/>
        <br />
        <br/>
        <label>email: </label>
        <input type="text" placeholder="e.g John@gmail.com"  onChange={handlerEmail}/>
        <br/>
        <br />
        <label>password: </label>
        <input type="text"   onChange={handlerPassword}/>
        <small>{error}</small>
        <br />
        <button> add user</button>
        </form>
    </div>
  )
}
export default Registration;
