import styles from "./Table.module.css";
import { useState } from "react";
import { v4 as uuidv4 } from "uuid";

function Table({ holidayData, updateFavs }) {
  const [isClicked, setIsClicked] = useState(false);

  const handleAdd = (data) => {
    const newFav = {
      id: uuidv4(),
      name: data.name,
      description: data.description,
      type: data.type,
      date: data.date.iso,
    };
    setIsClicked(true);
    updateFavs(newFav);
  };

  return (
    <div>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Type</th>
            <th>Date/Time</th>
            <th>Add to Favs</th>
          </tr>
        </thead>
        <tbody>
          {holidayData &&
            holidayData.map((data, i) => (
              <tr key={i}>
                <td>{data.name}</td>
                <td>{data.description}</td>
                <td>{data.type}</td>
                <td>{data.date.iso}</td>
                <td>
                  <p onClick={() => handleAdd(data)}>{isClicked ? "added" : "add"}</p>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}
export default Table;
