import { NavLink } from "react-router-dom";
import styles from "./Navbar.module.css";
import CurrencySelector from "./CurrencySelector";
import React, { useState, useEffect } from 'react';
import { Button } from './Button';

function Navbar({   isLoggedin,  setisLoggedin,  username,  updateUsername,  currency,  updateCurrency, }) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  
  useEffect(() => {
    const closeMenu = () => setClick(false);
    document.addEventListener('click', closeMenu);
    return () => {
      document.removeEventListener('click', closeMenu);
    };
  }, []);

  const logout = () => {
    setisLoggedin(false);
    updateUsername("");
    closeMobileMenu();
  };

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  const closeMobileMenu = () => setClick(false);

  return (
    <nav className={styles.navbar}>
      <div className="navbar-container">
        <NavLink to="/" className='navbar-logo' onClick={closeMobileMenu}>
          TRAVELBUD 
          <i class='fab fa-typo3' />
        </NavLink>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <NavLink to='/favorites' className='nav-links' onClick={closeMobileMenu}>
                Favs
              </NavLink>
            </li>
            <li className='nav-item'>
              <NavLink to='/' onClick={logout} className='nav-links' onClick={closeMobileMenu}>
                Logout
              </NavLink>
            </li>
            </ul>
            {button && <Button buttonStyle='btn--outline'>Logout</Button>}
      {isLoggedin && (
        <>
          <div>
            {username}{" "}
            <CurrencySelector
              currency={currency}
              updateCurrency={updateCurrency}
            />
          </div>
        </>
      )}
      </div>
    </nav>
  );
}

export default Navbar;
