import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import mockAPI from "../api/mockapi";
import Table from "./Table";
import styles from "./Results.module.css";

function Results() {
  const [holidayData, setHolidayData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const [searchParams, setSearchParams] = useSearchParams();
  const [country, setCountry] = useState(searchParams.get("country"));
  const [year, setYear] = useState(searchParams.get("year"));

  const apiGet = async () => {
    setIsLoading(true);
    try {
      const response = await mockAPI.get(
        `/holidays?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4&country=AI&year=2024`
      );
      setHolidayData(response.data.response.holidays);
      setIsLoading(false);
    } catch (error) {
      console.log(error.message);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    apiGet();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [country, year]);

  const checkRange = () => {
    console.log("check");
  };

  return (
    <div className={styles.results}>
      <h1>Holiday Data</h1>
      <label>From: </label>
      <input type="date" />
      <label> To: </label>
      <input type="date" />
      <button onClick={checkRange}>Check dates</button>
      {isLoading ? <p>Loading...</p> : <Table list={holidayData} />}
    </div>
  );
}
export default Results;
