//         },
//         body: JSON.stringify(item)
//     });

//     result = await result.json();
//     localStorage.setItem("user-info", JSON.stringify(result)
//     )
//   }

const apiGet = async () => {
    try {
      const response = await userApi.get(`/users/`);
      console.log("response.data", response.data);
      setUsers(response.data);
       } catch (error) {
      console.log(error.message);
    }
  }

  useEffect(() => {
    apiGet();

    if (setLoggedIn === true)
    {
        <Link to='/view'>View Product Details</Link>
    }
  }, []);


  const onSubmitHandler = () => {
   const newlist = users.filter((item) => item.email === email && item.password === password)

   if (Object.keys(newlist).length === 1) {
    setLoggedIn(true);
    localStorage.setItem("user-info", JSON.stringify(newlist));
   }
   else{
    setLoggedIn(false);
   }

  }

//   console.log(loggedIn);

  return (
    <div className="login">
      <input
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type="text"
        placeholder="Email"
      />
      <br></br>
      <input
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type="password"
        placeholder="Password"
      />
      <br></br>
      <small>this is error placeholder</small>
      <br></br>
      <button type="submit" onClick={onSubmitHandler}>Login</button>
    </div>
  );
}

export default Login;
