import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import mockAPI from "../api/mockAPI";

function Search({ username }) {
  const navigate = useNavigate();
  const [countryData, setCountryData] = useState([]);

  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/countries?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4`);
      console.log("response.data", response.data);
      setCountryData(response.data.response.countries);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    apiGet();
  }, []);

  const handlerSearch = () => {
    console.log("Search");
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p> Where would you like to go?</p>
      <form onSubmit={handlerSearch}>
        <label>I would like to visit: </label> 
        <select>
          <option value="">Select a country</option>
        </select>        <br />
        <br />
        <button className={styles.button} onClick={apiGet}>Get me some info!</button>
      </form>
    </div>
  );
}

export default Search;
