import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import mockAPI from "../api/mockAPI";

function Search({ username }) {
  const navigate = useNavigate();
  const [countryData, setCountryData] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");

  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/countries?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4`);
      console.log("response.data", response.data);
      setCountryData(response.data.response.countries);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    apiGet();
  }, []);

  const handlerSearch = () => {
    const countryCode = selectedCountry;
    console.log(`Search for country code ${countryCode}`);
    // Make a request to the API to get information about the selected country.
  };

  const handlerResult = (result) => {
    console.log(result);
    // Display the results in the Search component.
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p> Where would you like to go?</p>
      <form onSubmit={handlerSearch}>
        <label>I would like to visit: </label> 
        <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
          <option value="">Select a country</option>
          {countryData.map((country) => (
            <option key={country.country_code} value={country.country_code}>
              {country.country_name}
            </option>
          ))}
        </select>
        <br />
        <br />
        <button className={styles.button} onClick={apiGet}>Get me some info!</button>
      </form>
      <p>Results:</p>
      <ul>
        {result.map((country) => (
          <li key={country.country_name}>{country.country_name}</li>
        ))}
      </ul>
    </div>
  );
}

export default Search;
