import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import mockAPI from "../api/mockapi";
import Table from "../components/Table";
import React, { useEffect } from "react";

function Search({ username }) {
  const navigate = useNavigate();
  const [countryData, setCountryData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredCountryData, setFilteredCountryData] = useState([]);

  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/countries?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4`);
      console.log("response.data", response.data);
      setCountryData(response.data.response.countries);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    apiGet();
  }, []);

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
    setFilteredCountryData(countryData.filter((country) => {
      return country.name.toLowerCase().includes(searchTerm.toLowerCase());
    }));
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p> Where would you like to go?</p>
      <form onSubmit={handleChange}>
        <label>I would like to visit: </label> <input />
        <br />
        <br />
        <button className={styles.button}>
          Get me some info!
        </button>
      </form>
      <Table list={filteredCountryData} />
    </div>
  );
}

export default Search;
