import { NavLink } from "react-router-dom";
import styles from "./Navbar.module.css";
import CurrencySelector from "./CurrencySelector";
import React, { useState, useEffect } from 'react';

function Navbar({   isLoggedin,  setisLoggedin,  username,  updateUsername,  currency,  updateCurrency, }) {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  
  useEffect(() => {
    const closeMenu = () => setClick(false);
    document.addEventListener('click', closeMenu);
    return () => {
      document.removeEventListener('click', closeMenu);
    };
  }, []);

  const logout = () => {
    setisLoggedin(false);
    updateUsername("");
    closeMobileMenu();
  };

  return (
    <nav className={styles.navbar}>
      <div className="navbar-container">
        <NavLink to="/" className="navbar-logo">
          TRAVELBUD <i className='fab fa-typo3' />
        </NavLink>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <NavLink to='/favorites' className='nav-links' onClick={closeMobileMenu}>
                Favs
              </NavLink>
            </li>
            <li className='nav-item'>
              <NavLink to='/' onClick={logout} className='nav-links' onClick={closeMobileMenu}>
                Logout
              </NavLink>
            </li>
            <li>
            <NavLink to='/sign-up' className='nav-links-mobile' onClick={closeMobileMenu}>
                Sign Up
              </NavLink>
            </li>
            </ul>
      {isLoggedin && (
        <>
          <div>
            {username}{" "}
            <CurrencySelector
              currency={currency}
              updateCurrency={updateCurrency}
            />
          </div>
        </>
      )}
      </div>
    </nav>
  );
}

export default Navbar;
