import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from 'react';
import mockAPI from "../api/mockAPI";

function Search({ username }) {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [countryData, setCountryData] = useState([]);
  const [year, setYear] = useState('');

  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/countries?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4`);    
      console.log("response.data", response.data);
      setCountryData(response.data.response.countries);
       } catch (error) {
      console.log(error.message);
    }
  };

  const handlerSearch = () => {
    console.log("Search");
  };

  const handlerDate = () => {
    console.log("date changed");
  };
  
  useEffect(() => {
    apiGet();
  }, []);


  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p> Where would you like to go?</p>
      <form onSubmit={handlerSearch}>
        <label>I would like to visit: </label> 
        <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
          <option value="">Select a country</option>
          {countries.map((country) => (
            <option key={country.country_code} value={country.country_code}>
              {country.country_name}
            </option>
          ))}
        </select>
        <label>from:</label>
        <input
          type="text"
          name="name"
          placeholder="YYYY-MM-DD"
          onChange={handlerDate}
        />
        <label>to:</label>
        <input
          type="text"
          placeholder="YYYY-MM-DD"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <br />
        <br />
        <button className={styles.button} onClick={apiGet}>Get me some info!</button>
      </form>
    </div>
  );
}

export default Search;
