import { useState, useEffect } from "react";
import axios from "axios";
import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";

const iso3166 = require('iso-3166-1'); // Import iso-3166-1 library

function Search({ username }) {
  const navigate = useNavigate();
  const [country, setCountry] = useState("");
  const [year, setYear] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [countryList, setCountryList] = useState([]);

  useEffect(() => {
    const fetchCountryList = async () => {
      try {
        const response = await axios.get(
          "https://calendarific.com/api/v2/countries",{
            params: {
              api_key: "bec8af6c95730291984e596b210fc460f4bbacab",
            },
          }
        );
        console.log(response.data); // Check the response data in the console
        setCountryList(response.data.countries);
      } catch (error) {
        console.error("Error fetching country list:", error);
      }
    };
      
    fetchCountryList();
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submit");
    navigate(
      `/:id?country=${country}&year=${year}&fromDate=${fromDate}&toDate=${toDate}`
    );
  };

  const handleCountryChange = (event) => {
    setCountry(event.target.value);
  };

  const handleYearChange = (event) => {
    setYear(event.target.value);
  };

  const handleDateChange = (event) => {
    const { name, value } = event.target;
    if (name === "fromDate") {
      setFromDate(value);
    } else if (name === "toDate") {
      setToDate(value);
    }
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p>Where would you like to go?</p>
      <form onSubmit={handleSubmit}>
        <label>I would like to visit:</label>
        <select value={country} onChange={handleCountryChange}>
         <option value="">Select a country</option>
          {countryList.response.countries.map((country) => (
           <option key={country["iso-3166"]} value={country["iso-3166"]}>
           {country.country_name}
         </option>
         ))}
        </select>


        <label>Year:</label>
        <input
          type="text"
          name="year"
          placeholder="YYYY"
          value={year}
          onChange={handleYearChange}
        />

        <label>From:</label>
        <input
          type="text"
          name="fromDate"
          placeholder="YYYY-MM-DD"
          value={fromDate}
          onChange={handleDateChange}
        />

        <label>To:</label>
        <input
          type="text"
          name="toDate"
          placeholder="YYYY-MM-DD"
          value={toDate}
          onChange={handleDateChange}
        />

        <br />
        <br />
        <button className={styles.button} type="submit">
          Get me some info!
        </button>
      </form>
    </div>
  );
}

export default Search;
