import './App.css';
// App.js
import { useEffect, useState } from 'react';
import userApi from './api/userApi';
import Table from './components/Table';
import AddForm from './components/AddForm';
import TableUser from './components/TableUser';
import TableUserFav from './components/TableUserFav';
// import AddForm from './components/AddForm';

function App() {

  const [users, setUsers] = useState([]);
  const [usersFav, setUsersFav] = useState([]);


  const apiGet = async () => {
    try {
      const response = await userApi.get(`/users/`);
      console.log("response.data", response.data);
      setUsers(response.data);
       } catch (error) {
      console.log(error.message);
    }
  }


  const apiGetId = async (id) => {
    try {
      const response = await userApi(`/users/${id}/favorite`);
        console.log(response.data);  
        setUsersFav(response.data);  
    } catch (error) {
      console.log(error.message);
    }
  }

  const apiPost = async (newUser) => {
    try {
      const response = await userApi.post(`/users`, newUser)
      console.log(response.data);
      apiGet();
    } catch(error) {
      console.log(error.message);
    };
  }



  useEffect(() => {
    apiGet();
  }, []);


  return (


    <div className="App">
      <h1>Holiday Data</h1>
      {/* <button onClick={apiGet}>Load Holiday Data</button> */}
      {/* <Table list={holidayData} /> */}
      <AddForm handlerAddUser={apiPost} />
      <button onClick= {apiGet}>Load Users</button>
      <TableUser list={users} />
      <br></br>
      <button onClick= {apiGetId(1)}>Load Users fav</button>
      {/* <TableUserFav list={usersFav} /> */}

      <AddFav handlerAddUser={apiPost} />

    </div>
  );
}
export default App;
