import { useState, useEffect } from "react";
import { createSearchParams, useNavigate } from "react-router-dom";
import styles from "./Search.module.css";
import axios from "axios";

const iso3166 = require("iso-3166-1");

function Search({ username }) {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [year, setYear] = useState("2023");
  const [isLoading, setIsLoading] = useState("false");

  const navigate = useNavigate();
  const API_KEY = process.env.REACT_APP_API_KEY;

  useEffect(() => {
    const fetchCountries = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(
          "https://calendarific.com/api/v2/countries",
          {
            params: {
              api_key: '70ba78bfece8b04c18abb38026b41cf024134ef4',
            },
          }
        );
        setCountries(response.data.response.countries);
        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };
    fetchCountries();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const country = iso3166.whereCountry(selectedCountry);
    const countryCode = country ? country.alpha2 : null;
    navigate({
      pathname: "/results",
      search: `?${createSearchParams({
        country: countryCode,
        year: year,
      })}`,
    });
  };

  return (
    <div className={styles.form}>
      <div className="image">
      <div className="welcome-to-travelbud-wrapper">
      <img className="welcome-to-travelbud" alt="Welcome to travelbud" src="/searchbg.JPG" />      <h2>{`Hi, ${username}!`}</h2>
      <p> Where would you like to go?</p>
      <br />
      <form onSubmit={handleSubmit}>
        <label>I would like to visit: </label>
        <select
          className={styles.select}
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">
            {isLoading ? "Loading..." : "Select a country"}
          </option>
          {countries.map((country, i) => (
            <option key={i} value={country.country_name}>
              {country.country_name}
            </option>
          ))}
        </select>
        <br />
        <br />
        <label2> in: </label2>
        <input
          className={styles.year}
          type="number"
          min="1900"
          max="2099"
          step="1"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <br />
        <br />
        <br />
        <button
          className={styles.button}
          type="submit"
          disabled={selectedCountry ? false : true}
        >
          Get me some info!
        </button>
      </form>
        </div>
      </div>
    </div>
  );
}

export default Search;
