import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";

const Search = ({ username }) => {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [year, setYear] = useState('');

  const apiGet = async () => {
    try {
      const response = await axios.get(
        `https://calendarific.com/api/v2/countries?api_key=YOUR_API_KEY`
      );
      setCountries(response.data.response.countries);
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    apiGet();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();

    const selectedCountryObj = countries.find(
      (country) => country.country_name === selectedCountry
    );

    if (selectedCountryObj) {
      navigate(`/${selectedCountryObj.country_name}`);
    }
  };

  // Filter countries based on selectedCountry
  const filteredCountries = countries.filter((country) =>
    country.country_name.toLowerCase().includes(selectedCountry.toLowerCase())
  );

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p>Where would you like to go?</p>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Country"
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        />
        <ul>
          {filteredCountries.map((country) => (
            <li
              key={country.country_name}
              onClick={() => setSelectedCountry(country.country_name)}
            >
              {country.country_name}
            </li>
          ))}
        </ul>
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default Search;
