import styles from "./Login.module.css"; // Import styles from the CSS module

function Login({ setIsloggedin, updateUsername, currency, updateCurrency }) {
  const [input, setInput] = useState("");
  const navigate = useNavigate();
  const [backgroundImage, setBackgroundImage] = useState("https://pixabay.com/photos/nature-outdoors-travel-exploration-2990060/");

