  return (
    <ImageBackground
      source={{ uri: backgroundImage }}
      style={styles.background} // Use the imported styles object for the ImageBackground
    >
      <View style={styles.app}>
        <h1>Welcome to TravelBud!</h1>
        <p>Please enter your name to start using the app</p>
        {/* Rest of the JSX code */}
        {/* ... */}
        <button
          style={styles.button} // Use the imported styles object for the button style
          disabled={!input} // Use logical NOT operator for the disabled attribute
          onClick={handleClick}
        >
          Login
        </button>
      </View>
    </ImageBackground>
  );
}

export default Login;
