import styles from "./Favorites.module.css";
import { useNavigate } from "react-router-dom";

function Favorites({ favs, updateFavNotes, deleteFav }) {
  const navigate = useNavigate();

  const saveNotes = (e, id) => {
    const favNotes = e.target.value;
    setNotes(favNotes);
  };

  const handleDelete = (id) => {
    const newFavs = favs.filter((fav) => fav.id !== id);
    deleteFav(newFavs);
  };

  return (
    <div className={styles.fav}>
      <button className={styles.back} onClick={() => navigate(-1)}>
        {`« Back to Results`}
      </button>
      <h1>Favorites</h1>
      <div>
        {favs.length !== 0 ? (
          <table className={styles.table}>
            <thead>
              <tr>
                <th>Country</th>
                <th>From</th>
                <th>To</th>
                <th>Personal Notes</th>
                <th>Remove</th>
              </tr>
            </thead>
            <tbody>
              {favs.map((fav, i) => (
                <tr key={i}>
                  <td>{fav.country}</td>
                  <td>{fav.start}</td>
                  <td>{fav.end}</td>
                  <td><textarea value={fav.notes.length ? fav.notes : ""} rows="3" cols="50" maxLength="150" onChange={() => saveNotes(fav.id)}></textarea></td>
                  <td><button onClick={() => handleDelete(fav.id)}>x</button></td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No favorites yet..</p>
        )}
      </div>
    </div>
  );
}

export default Favorites;
