import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { iso31661 } from "iso-3166";
import styles from "./Search.module.css";
import axios from "axios";

function Search({ username }) {
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [year, setYear] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get(
          "https://calendarific.com/api/v2/countries",
          {
            params: {
              api_key: "bec8af6c95730291984e596b210fc460f4bbacab",
            },
          }
        );
        setCountries(response.data.response.countries);
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, []);

  const handlerSubmit = () => {
    console.log("Submit");
  };
  const handleFromDate = () => {
    console.log("date changed");
  };
  const handleToDate = () => {
    console.log("date changed");
  };
  const handleClick = () => {
    navigate(":id");
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}!`}</h2>
      <p> Where would you like to go?</p>
      <br />
      <form onSubmit={handlerSubmit}>
        <label>I would like to visit: </label>{" "}
        <select
          value={selectedCountry}
          onChange={(e) => setSelectedCountry(e.target.value)}
        >
          <option value="">Select a country</option>
          {countries.map((country, i) => (
            <option key={i} value={country.country_code}>
              {country.country_name}
            </option>
          ))}
        </select> {" "}
        <label>in: </label>
        {/* <label>from:</label> {" "}
        <input type="date" onChange={handleFromDate} />
        <label>to:</label> {" "}
        <input type="date" onChange={handleToDate} /> */}
        <br />
        <br />
        <br />
        <button className={styles.button} onClick={handleClick}>
          Get me some info!
        </button>
      </form>
    </div>
  );
}

export default Search;
