import styles from "./Login.module.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import CurrencySelector from "./CurrencySelector";

function Login({ setIsloggedin, updateUsername, currency, updateCurrency }) {
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  const handleClick = () => {
    updateUsername(input);
    navigate("/search");
    setIsloggedin(true);
  };

  return (
    <div className={styles.login}>
    <div style={{ 
      backgroundImage: "url(/loginbg.JPG)",
      backgroundPosition: 'contain',
      backgroundSize: 'cover',
      backgroundRepeat: 'no-repeat',
      opacity: 0.8,
      height: 600,
      width: 1000 }}> </div>
      <div class="centered">
      <h1>Welcome to TravelBud!</h1>
      <br />
      <h2>Please enter your name to start using app</h2>
      <br />
      <label>Name: </label>
      <input
        type="text"
        placeholder="e.g. John"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <br />
      <br />
      <label2>Select your currency: </label2>
      <CurrencySelector currency={currency} updateCurrency={updateCurrency} />
      <br />
      <br />
      <br />
      <button
        className={styles.button}
        disabled={input ? false : true}
        onClick={handleClick}
      >
        Start Your Journey
      </button>
      </div>
    </div>
  );
}

export default Login;
