import styles from "../css-modules/Login.module.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import CurrencySelector from "../components/CurrencySelector";
import useUserState from "../components/userStateContext";

function Login() {
  const { updateLogIn, updateUsername } = useUserState();
  const [input, setInput] = useState("");
  const navigate = useNavigate();

  const handleClick = () => {
    updateUsername(input);
    updateLogIn();
    navigate("/search");
  };

  return (
    <div className={styles.login}>
      <h1>Welcome to TravelBud!</h1>
      <p>Please enter your name to start using app</p>
      <br />
      <label>Name: </label>
      <input
        type="text"
        placeholder="e.g. John"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <br />
      <br />
      <label>Select your currency: </label>
      <CurrencySelector />
      <br />
      <br />
      <br />
      <button
        className={styles.button}
        disabled={input ? false : true}
        onClick={handleClick}
      >
        Login
      </button>
    </div>
  );
}

export default Login;
