import './App.css';
// App.js
import { useEffect, useState } from 'react';
import mockAPI from './api/mockapi';
import Table from './components/Table';
// import AddForm from './components/AddForm';

function App() {

  const [users, setUsers] = useState([]);


  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/users/`);
      console.log("response.data", response.data);
      setUsers(response.data);
       } catch (error) {
      console.log(error.message);
    }
  }


  const apiGetId = async (id) => {
    try {
      const response = await mockAPI(`/users/${id}`);
        console.log(response.data);    
    } catch (error) {
      console.log(error.message);
    }
  }

  const apiPost = async (newUser) => {
    try {
      const response = await mockAPI.post(`/users`, newUser)
      console.log(response.data);
      apiGet();
    } catch(error) {
      console.log(error.message);
    };
  }



  useEffect(() => {
    apiGet();
  }, []);


  return (


    <div className="App">
      <h1>Holiday Data</h1>
      {/* <button onClick={apiGet}>Load Holiday Data</button> */}
      {/* <Table list={holidayData} /> */}
      <AddForm handlerAddUser={apiPost} />
      <button onClick= {apiGet}>Load Users</button>
      <Table list={users} />




    </div>
  );
}
export default App;
