import styles from "./Favorites.module.css";
import React, { useState } from "react";

const Favorites = ({ favorites, handleUpdate, handleCancel }) => {
  const [countryName, setCountryName] = useState("");
  const [date, setDate] = useState("");
  const [notes, setNotes] = useState("");
  const [submittedFavorites, setSubmittedFavorites] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedFavorites = {
      id: null,
      countryName: countryName,
      date: date,
      notes: notes,
    };

    // Add the submitted favorites to the list
    setSubmittedFavorites((prevFavorites) => [...prevFavorites, updatedFavorites]);

    // Clear the input fields after submission
    setCountryName("");
    setDate("");
    setNotes("");
  };

  return (
    <div className={styles.fav}>
      <h2>Favorites</h2>
      <form onSubmit={handleSubmit}>
        <label>Country Name:</label>
        <input
          type="text"
          placeholder="Country"
          value={countryName}
          onChange={(e) => setCountryName(e.target.value)}
        />
        <br />
        <label>Date:</label>
        <input
          type="number"
          placeholder="Date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <br />
        <label>Notes:</label>
        <input
          type="text"
          placeholder="Things to Note"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
        />
        <br />
        <button type="submit">Submit</button>
        <button type="button" onClick={handleCancel}>
          Cancel
        </button>
      </form>

      {/* Display the submitted favorites */}
      <div>
        <h3>Submitted Favorites:</h3>
        <ul>
          {submittedFavorites.map((favorite, index) => (
            <li key={index}>
              <strong>Country:</strong> {favorite.countryName},{" "}
              <strong>Date:</strong> {favorite.date}, <strong>Notes:</strong>{" "}
              {favorite.notes}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Favorites;
