import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from "./Search.module.css";
import { useNavigate } from "react-router-dom";

const Search = ({ username }) => {
  const navigate = useNavigate();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [year, setYear] = useState('');

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await axios.get('https://calendarific.com/api/v2/countries', {
          params: {
            api_key: 'bec8af6c95730291984e596b210fc460f4bbacab',
          },
        });
        setCountries(response.data.response.countries);
      } catch (error) {
        console.error('Error fetching countries:', error);
      }
    };

    fetchCountries();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();

    // Handle search logic using selectedCountry and year states

    // Example navigation code:
    navigate(`/${selectedCountry}`);
  };

  return (
    <div className={styles.form}>
      <h2>{`Hi, ${username}`}</h2>
      <p>Where would you like to go?</p>
      <form onSubmit={handleSearch}>
        <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
          <option value="">Select a country</option>
          {countries.map((country) => (
            <option key={country.country_code} value={country.country_code}>
              {country.country_name}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default Search;
