import "./App.css";
import { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import Login from "./components/Login";
import Search from "./components/Search";
import Results from "./components/Results";
import NotFound from "./components/NotFound";
import Favorites from "./components/Favorites";
import Table from "./components/Table";
import mockAPI from "../api/mockAPI";

function App() {
  const [username, setUsername] = useState("guest");
  const [countryData, setCountryData] = useState([]);
  const apiGet = async () => {
    try {
      const response = await mockAPI.get(`/countries?&api_key=e6bc2f9624d494ca421477e718736a0b6ec483b4`);    
      console.log("response.data", response.data);
      setCountryData(response.data.response.countries);
       } catch (error) {
      console.log(error.message);
    }
  }

  const updateUsername = (value) => {
    setUsername(value);
  };

  useEffect(() => {
    apiGet();
  }, []);


  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Layout />}>
            <Route index element={<Login updateUsername={updateUsername} />} />
            <Route path="search" element={<Search username={username} />} />
            <Route path="favorites" element={<Favorites/>} />
            <Route path=":id" element={<Results />} />
            <Route path="*" element={<NotFound />} />
          </Route>
        </Routes>
      </BrowserRouter>
      <Table list={countryData} />
    </>
  );
}
export default App;
